    // Use Cypress's built-in login command
    login();

    // Take a screenshot of the page after login
    cy.wait(5000);
    cy.screenshot('login_page');

    // Navigate to the main page
    cy.visit('/');

    // Take another screenshot of the page after navigating to the main page
    cy.wait(5000);
    cy.screenshot('main_page');

    // Logout
