    std::string html;

    // Replace Markdown headers with HTML headers
    std::regex markdownHeaderRegex("^# (.*)$");
    std::string replacement = "<h1>$1</h1>";
    html = std::regex_replace(markdown, markdownHeaderRegex, replacement);

