def find_median(arr1, arr2):
    n1 = len(arr1)
    n2 = len(arr2)

    # Find the middle indices of both arrays
    mid1 = n1 // 2
    mid2 = n2 // 2

    # Initialize pointers to the middle elements
    left = right = 0
    left_mid = mid1
    right_mid = mid2

    # Compare the middle elements and return the median
    while left <= left_mid and right <= right_mid:
        if arr1[left] <= arr2[right]:
            if left_mid == 0 or arr1[left - 1] <= arr2[right]:
                left += 1
            else:
